package fundamentals;
/*
 * > Greater Then
 * < less than
 * >= Greater then equal to
 * <= less then equal to
 * == exactly equal to
 * != not equal to
 */
public class RelationalOper {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		
		System.out.println("Is A greater then B?:-"+(a>b));
		System.out.println("Is A less then B?:-"+(a<b));
		
		int m=8;
		int n=8;
		System.out.println("Is M greater thenor equal to N?:-"+(m>=n));
		System.out.println("Is M Less then or equal to N?:-"+(m<=n));
		System.out.println("Is M is exactly equal to N?:-"+(m==n));
		System.out.println("Is M is not equal to N?:-"+(m!=n));
	}

}
