package statementsAndLoops;

public class IfDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * If else is type of statement.
		 * we use it to put constraint condition in our code.
		 * Block of code execute only if condition is setisfied or 
		 * else block is execute
		 */
		
		double marks=80;
		 if (marks>=60) {
			 System.out.println(" I'm happy ");
		 }
		 else {
			 System.out.println(" I'm Sad ");
		 }
	}

}
